mapper.select:
SELECT C.AR_ID , C.SBJ , C.CONT , C.MBR_ID C_MBR_ID , C.CRT_DT , C.MDFY_DT , C.RD_CNT , C.MN_ID C_MN_ID , M.MBR_ID , M.NCK_NM , M.AUTH , MN.MN_ID , MN.MN_NM , MN.PRNT_MN_ID , MN.AUTH MN_AUTH FROM CLB C , MBR M , MN WHERE C.MBR_ID = M.MBR_ID AND MN.AUTH = M.AUTH AND MN.MN_ID = C.MN_ID AND MN.MN_ID = #{menuId}
AND C.SBJ LIKE '%' || #{subjectKeyword} || '%'
ORDER BY MDFY_DT DESC, AR_ID DESC

mapper.insert:
INSERT INTO CLB	(
					AR_ID
					, SBJ
					, CONT
					, MBR_ID
					, CRT_DT
					, MDFY_DT
					, RD_CNT
					, MN_ID
				)
VALUES			(
					'AR-' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(AR_ID_SEQ.NEXTVAL, '6', 0)
					, #{subject}
					, #{content}
					, #{memberId}
					, SYSDATE
					, SYSDATE
					, 0
					, #{menuId}
				)
